package mate.academy;import mate.academy.model.FruitTransaction;import mate.academy.service.*;import mate.academy.service.impl.*;import mate.academy.service.transaction.*;import mate.academy.strategy.TransactionStrategy;import mate.academy.strategy.TransactionStrategyImpl;import java.util.List;import java.util.Map;public class Main {    private static final String inputFilePath = "src/main/java/mate/academy/resources/database.csv";    private static final String reportFilePath = "src/main/java/mate/academy/resources/report.csv";    private static final ReaderService readerService = new ReaderServiceImpl();    private static final ParserService parserService = new ParserServiceImpl();    private static final ProcessDataService processDataService = new ProcessDataServiceImpl();    private static final ReportService reportService = new ReportServiceImpl();    private static final WriterService writerService = new WriterServiceImpl();    public static void main(String[] args) {        Map<FruitTransaction.Operation, TransactionHandler> transactionHandlersMap = Map.of(                FruitTransaction.Operation.BALANCE, new BalanceHandlerImpl(),                FruitTransaction.Operation.PURCHASE, new PurchaseHandlerImpl(),                FruitTransaction.Operation.SUPPLY, new SupplyHandlerImpl(),                FruitTransaction.Operation.RETURN, new ReturnHandlerImpl()        );        List<String> inputData = readerService.readFromFile(inputFilePath);        List<FruitTransaction> transactions = parserService.parseData(inputData);        TransactionStrategy transactionStrategy =                new TransactionStrategyImpl(transactionHandlersMap);        processDataService.processTransactions(transactions, transactionStrategy);        String report = reportService.createReport();        writerService.writeToFile(report, reportFilePath);    }}